package com.example.kotlinuniversityfinalproject.presentation.first

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.core.os.bundleOf
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.kotlinuniversityfinalproject.R
import com.example.kotlinuniversityfinalproject.data.local.CoffeeDC
import com.example.kotlinuniversityfinalproject.databinding.FragmentFirstfragmentBinding
import com.example.kotlinuniversityfinalproject.presentation.second.SecondFragm


class FirstFragm : Fragment() {

    private var _binding: FragmentFirstfragmentBinding? = null
    private val binding get() = _binding!!
    var MyCoffee = ArrayList<CoffeeDC>()
    lateinit var adapter: FirstAdapter


    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        var coffeeOne = CoffeeDC(
            getString(R.string.textCoffee),
            "Coffee has been around since 800 A.D.\n" +
                    "According to legend, 9th-century goat herders discovered how caffeine affected their goats, who looked like they were \"dancing\" (probably high on caffeine) after eating the Coffea plant's fruit. After that discovery, the cup of coffee as we know it was born when a local monk made a drink with the product and discovered that it kept him awake all night.",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTdGUJ2NXaPSvvtNl4qfpwnmNFNxVJ4Bk7eIw&usqp=CAU"
        )
        var coffeeTwo = CoffeeDC(
            getString(R.string.textCoffee),
            "Technically, coffee beans are seeds\n" +
                    "They're the pits of the cherry-like berries found on flowering shrubs, but because of their resemblance to legumes, we name them \"beans.\" So, technically coffee beans are seeds.",
            "data:image/jpeg;base64,"
        )
        var coffeeThree = CoffeeDC(
            getString(R.string.textCoffee),
            "Brazil is the world's largest coffee producer\n" +
                    "According to the International Coffee Organization, Brazil now produces about a third of the world's coffee supply, roughly twice as much as the runner-up, Vietnam.",
            "data:image/jpeg;base64,"
        )
        var coffeeFour = CoffeeDC(
            getString(R.string.textCoffee),
            "Finland drinks the most coffee in the world\n" +
                    "Which country drinks the most coffee? According to the International Coffee Organization, the average adult Finn consumes 27.5 pounds of coffee per year. In comparison, the average weight of an American is only 11 pounds.\n" +
                    "\n" +
                    "In second place, you find Norway, third place Iceland and fourth place Denmark.",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTeDO-KctpZxgr-uELk1kbbgjsFs9aSiQ7bYw&usqp=CAU"
        )
        MyCoffee.add(coffeeOne)
        MyCoffee.add(coffeeTwo)
        MyCoffee.add(coffeeThree)
        MyCoffee.add(coffeeFour)
        adapter = FirstAdapter{ itemView ->
            val bundle = bundleOf(
                SecondFragm.number to itemView,
                SecondFragm.randFact to MyCoffee[itemView].factAboutCoffee,

                SecondFragm.nameof to MyCoffee[itemView].name,

                SecondFragm.url to MyCoffee[itemView].URL)
            findNavController().navigate(R.id.action_firstfragment_to_secondfragment, bundle)



        }
        adapter.setArrayListOfAnimals(MyCoffee)
    }


    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {

        _binding = FragmentFirstfragmentBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        val layoutManager = LinearLayoutManager(context)
        binding.rv.layoutManager = layoutManager
        binding.rv.adapter = adapter

    }

    override fun onDestroy() {
        super.onDestroy()
        _binding = null
    }


}
